mapper.update:
		UPDATE attendance_${year}
    	
	      	
	        	att_ord = #{attOrd},
	      	
	    
		WHERE 1=1
			AND member_id = #{memberId} 
			AND att_yn = #{attYn}
			
			
				
					AND month > 6 
				
				
					
						AND month < 7

		UPDATE attendance_${year}
    	SET
			mem_state = #{memState}
		WHERE 1=1
			AND member_id = #{memberId}
			AND month = #{month}

UPDATE attendance_${year}
SET group_grade = #{groupGrade}
WHERE 1=1
	AND member_id = #{memberId}
	AND month = #{month}

UPDATE attendance_${year}
SET ${removeFlag} = ''
WHERE 1=1
	AND id = #{id}
