mapper.update:
		UPDATE member SET
        	mem_state_date = now()
		WHERE id = #{id}

UPDATE member SET
	memo = #{memo}
	, memo_flag = #{memoFlag}
	, mod_date = now()
WHERE 
	id = #{id}

mapper.select:
SELECT COUNT(*) FROM ( SELECT m.id FROM member m LEFT OUTER JOIN depart d ON d.id = m.depart_id LEFT OUTER JOIN team t ON t.id = m.team_id LEFT OUTER JOIN attendance_${year} at ON at.member_id = m.id AND at.month = #{month} LEFT OUTER JOIN `group` g ON g.id = at.group_id LEFT OUTER JOIN same_period sp ON sp.id = m.same_period_id WHERE 1=1 AND m.del_yn = 'N' AND m.mem_state NOT IN ('6')
AND t.id = #{teamId}
AND (t.id = '4' or t.id = '8')
AND at.group_id = #{groupId}
AND m.mem_state = #{memStateId}
AND m.name LIKE CONCAT ('%', #{nameKW}, '%') AND m.mem_state NOT IN ('5')
GROUP BY m.id ) a

SELECT m.id , m.name , m.guider , m.depart_id , m.team_id , m.date_of_birth , m.htel , m.gender , m.member_reg_date , m.member_grad_date , m.same_period_id , m.origin_profile_img , m.replace_profile_img , m.reg_user , m.reg_date , m.mod_date , m.mem_state , m.del_yn , d.d_name as d_d_name , t.id as t_id , t.t_short_name as t_t_short_name , at.group_id as at_group_id , g.id as g_id , g.g_name as g_g_name , sp.birth_year as sp_birth_year FROM member m LEFT OUTER JOIN depart d ON d.id = m.depart_id LEFT OUTER JOIN team t ON t.id = m.team_id LEFT OUTER JOIN attendance_${year} at ON at.member_id = m.id AND at.month = #{month} LEFT OUTER JOIN `group` g ON g.id = at.group_id LEFT OUTER JOIN same_period sp ON sp.id = m.same_period_id WHERE 1=1 AND m.del_yn = 'N' AND m.mem_state NOT IN ('6')
AND t.id = #{teamId}
AND (t.id = '4' or t.id = '8')
AND at.group_id = #{groupId}
AND m.mem_state = #{memStateId}
AND m.name LIKE CONCAT ('%', #{nameKW}, '%') AND m.mem_state NOT IN ('5')
GROUP BY m.id ORDER BY
t.id ASC ,
m.name ASC LIMIT #{startRow}, #{boardSize}
